import React, { useState, useEffect } from "react";
import queryString from 'query-string';
import io from "socket.io-client";

import TextContainer from '../TextContainer/TextContainer';
import Messages from '../Messages/Messages';
import InfoBar from '../InfoBar/InfoBar';
import Input from '../Input/Input';
import Video from '../Video/Video';

import './Chat.css';

const ENDPOINT = 'localhost:5000';

let socket;

const Chat = ({ location }) => {
  const [name, setName] = useState('');
  const [room, setRoom] = useState('');
  const [users, setUsers] = useState('');
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [URL, setURL] = useState('');

//step 1 - ek aur useEffect mai likho ki data dedo aur callback mai likho emit karne ko ...like khud ka player state same kar de 
//step 5 - receive karne 



  useEffect(() => {
    const { name, room ,URL} = queryString.parse(location.search);

    socket = io(ENDPOINT);

    setRoom(room);
    setName(name);
    setURL(URL)

    socket.emit('join', { name, room, URL }, (error) => {
      if(error) {
        alert(error);
      }
    });
  }, [ENDPOINT, location.search]);

useEffect(()=>{
  socket.on('newuser', ()=>{
    console.log("new user joined");
  })
  },);

  useEffect(() => {
    socket.on('message', message => {
      setMessages(messages => [ ...messages, message ]);
    });

    socket.on("roomData", ({ users }) => {
      setUsers(users);
    });
}, []);

  const sendMessage = (event) => {
    event.preventDefault();

    if(message) {
      socket.emit('sendMessage', message, () => setMessage(''));
    }
  }

  const url2 = URL.toString();

  return (
    <div className="outerContainer">
      <Video URL={url2}/>
      <div className="container">
          <InfoBar room={room} />
          <Messages messages={messages} name={name} />
          <Input message={message} setMessage={setMessage} sendMessage={sendMessage} />
      </div>
      {/* <TextContainer users={users}/> */}
      {/* <button onClick= {()=> console.log(URL)}>Click Chat.js</button> */}


    </div>
  );
}

export default Chat;
